SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_proposta_excluir_inclusao ( nr_seq_inclusao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_inclusao_benef_w		smallint;
nr_seq_contrato_w		bigint;
nm_pessoa_fisica_w		varchar(60);
nm_mae_w			varchar(60);
cd_pessoa_fisica_w		varchar(20);
nr_cpf_w			varchar(11);
nr_seq_lote_inclusao_w		pls_lote_inclusao_benef.nr_sequencia%type;
nr_seq_proposta_w		pls_proposta_adesao.nr_sequencia%type;
ie_situacao_w			pls_lote_inclusao_benef.ie_situacao%type;
qt_anexos_w        smallint;


BEGIN

select	count(1)
into STRICT	qt_anexos_w
from	pls_inclusao_anexo
where	nr_seq_inclusao = nr_seq_inclusao_p;

if (qt_anexos_w > 0)	then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1058649);
end if;

select	nm_pessoa_fisica,
	nr_seq_contrato,
	nm_mae,
	cd_pessoa_fisica,
	nr_cpf,
	nr_seq_lote_inclusao
into STRICT	nm_pessoa_fisica_w,
	nr_seq_contrato_w,
	nm_mae_w,
	cd_pessoa_fisica_w,
	nr_cpf_w,
	nr_seq_lote_inclusao_w
from	pls_inclusao_beneficiario
where	nr_sequencia	= nr_seq_inclusao_p;

delete	FROM pls_inconsist_incl_benef
where	nr_seq_solic_inclusao	= nr_seq_inclusao_p;

delete	FROM pls_inclusao_benef_alt
where	nr_seq_inclusao_benef = nr_seq_inclusao_p;

delete	FROM pls_homonimo_pessoa_fisica
where	nr_seq_inclusao_benef	= nr_seq_inclusao_p;

delete	FROM pls_inclusao_beneficiario
where	nr_sequencia	= nr_seq_inclusao_p;

if (nr_seq_lote_inclusao_w IS NOT NULL AND nr_seq_lote_inclusao_w::text <> '') then
	select	count(1)
	into STRICT	qt_inclusao_benef_w
	from	pls_inclusao_beneficiario
	where	nr_seq_lote_inclusao	= nr_seq_lote_inclusao_w
	and	ie_status not in ('R','RO','A','B','C','I')
	and	coalesce(nr_seq_proposta::text, '') = ''
	and	coalesce(ie_tipo_operacao,'I')	= 'I'  LIMIT 1;

	if (qt_inclusao_benef_w = 0) then
		/*Mudar a situação da proposta para IP - integrado a proposta*/

		select	max(nr_seq_proposta)
		into STRICT	nr_seq_proposta_w
		from	pls_inclusao_beneficiario
		where	nr_seq_lote_inclusao	= nr_seq_lote_inclusao_w
		and	ie_status in ('A','B','C');

		update	pls_lote_inclusao_benef
		set	ie_situacao		= 'IP',
			nr_seq_proposta		= nr_seq_proposta_w
		where	nr_sequencia		= nr_seq_lote_inclusao_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
