CREATE OR REPLACE PROCEDURE pls_proposta_gerar_contrato_js (nr_seq_proposta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_proposta_p bigint, nr_contrato_p bigint, nr_seq_contrato_retorno_p INOUT bigint, nr_contrato_retorno_p INOUT bigint) AS $body$
BEGIN
 
CALL pls_proposta_gerar_contrato(nr_seq_proposta_p, nm_usuario_p, cd_estabelecimento_p);
 
if (ie_tipo_proposta_p in (2,4,6,8,9)) then 
	nr_seq_contrato_retorno_p	:= pls_obter_seq_contrato(nr_contrato_p);
	nr_contrato_retorno_p		:= nr_contrato_p;
else 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_contrato_retorno_p 
	from	pls_contrato 
	where	nr_seq_proposta	= nr_seq_proposta_p;
	 
	select	max(nr_contrato) 
	into STRICT	nr_contrato_retorno_p 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_retorno_p;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
