CREATE OR REPLACE PROCEDURE pls_proposta_gerar_pagador ( nr_seq_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_estipulante_pagador_w	varchar(1);
qt_contar_w			bigint;
cd_estipulante_w		varchar(10);
cd_cgc_estipulante_w		varchar(14);


BEGIN

select	ie_estipulante_pagador,
	cd_estipulante,
	cd_cgc_estipulante
into STRICT	ie_estipulante_pagador_w,
	cd_estipulante_w,
	cd_cgc_estipulante_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

if (ie_estipulante_pagador_w	= 'S') then
	select	count(*)
	into STRICT	qt_contar_w
	from	pls_proposta_pagador
	where	cd_pagador		= cd_estipulante_w
	or	cd_cgc_pagador		= cd_cgc_estipulante_w
	and	nr_seq_proposta		= nr_seq_proposta_p;

	if (qt_contar_w	= 0) then
		insert into pls_proposta_pagador(nr_sequencia,  nr_seq_proposta, nm_usuario,
			dt_atualizacao, cd_cgc_pagador,  cd_pagador)
		values (nextval('pls_proposta_pagador_seq'),nr_seq_proposta_p, nm_usuario_p,
			clock_timestamp(), cd_cgc_estipulante_w, cd_estipulante_w);
	  end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
