SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_proposta_liberar_inco_doc ( nr_seq_inconsist_doc_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Liberar as inconsistências de documentos da proposta de adesão.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_proposta_w 		pls_proposta_adesao.nr_sequencia%type;
qt_inconsistencia_w		integer;
qt_inconsistencia_doc_w		integer;


BEGIN

select	nr_seq_proposta
into STRICT	nr_seq_proposta_w
from	pls_proposta_inconsist_doc
where	nr_sequencia = nr_seq_inconsist_doc_p;

update	pls_proposta_inconsist_doc
set	dt_liberacao = clock_timestamp(),
	nm_usuario_liberacao = nm_usuario_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_seq_inconsist_doc_p;

select	count(*)
into STRICT	qt_inconsistencia_w
from	pls_proposta_validacao
where	nr_seq_proposta	= nr_seq_proposta_w
and	coalesce(dt_liberacao::text, '') = '';

select	count(1)
into STRICT	qt_inconsistencia_doc_w
from	pls_proposta_inconsist_doc
where	nr_seq_proposta = nr_seq_proposta_w
and	coalesce(dt_liberacao::text, '') = '';

if (qt_inconsistencia_w > 0) or (qt_inconsistencia_doc_w > 0) then
	update	pls_proposta_adesao
	set	ie_inconsistencia = 'S'
	where	nr_sequencia = nr_seq_proposta_w;
else
	update	pls_proposta_adesao
	set	ie_inconsistencia = 'N'
	where	nr_sequencia = nr_seq_proposta_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
