CREATE OR REPLACE PROCEDURE pls_proposta_lib_inco_doc_list (nr_seq_inconsist_lista_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Liberar todas as inconsistências de documentos da proposta de adesão, selecionadas no grid.

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_inconsist_lista_w	varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		bigint;
nr_seq_inconsistencia_w		bigint;


BEGIN

nr_seq_inconsist_lista_w	:= nr_seq_inconsist_lista_p;

while(nr_seq_inconsist_lista_w IS NOT NULL AND nr_seq_inconsist_lista_w::text <> '') loop
	begin
	tam_lista_w	:= length(nr_seq_inconsist_lista_w);
	ie_pos_virgula_w	:= position(',' in nr_seq_inconsist_lista_w);

	if (ie_pos_virgula_w <> 0) then
		begin
		nr_seq_inconsistencia_w		:= substr(nr_seq_inconsist_lista_w, 1, (ie_pos_virgula_w - 1));
		nr_seq_inconsist_lista_w	:= substr(nr_seq_inconsist_lista_w, (ie_pos_virgula_w + 1), tam_lista_w);

		CALL pls_proposta_liberar_inco_doc(
			nr_seq_inconsistencia_w,
			nm_usuario_p,
			cd_estabelecimento_p);
		end;
	end if;
	end;
end loop;

end;
