CREATE OR REPLACE PROCEDURE pls_proposta_limpar_declaracao ( nr_sequencia_p bigint, cd_beneficiario_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_declaracao_w		bigint;
nr_seq_proposta_benef_w		bigint;
nr_seq_analise_w		bigint;
nr_seq_solicitacao_w		bigint;
nr_seq_deferimento_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_proposta_benef_w
from	pls_proposta_beneficiario
where	cd_beneficiario = cd_beneficiario_p;

select	max(nr_sequencia)
into STRICT	nr_seq_analise_w
from	pls_analise_adesao
where	nr_seq_pessoa_proposta = nr_seq_proposta_benef_w;

select	max(nr_sequencia)
into STRICT	nr_seq_solicitacao_w
from	pls_analise_solicitacao
where	nr_seq_analise = nr_seq_analise_w;

select	max(nr_sequencia)
into STRICT	nr_seq_deferimento_w
from	pls_analise_deferimento
where	nr_seq_solicitacao = nr_seq_solicitacao_w;

delete from pls_entrevista_analise
where	nr_seq_analise = nr_seq_analise_w;

delete from pls_analise_deferimento
where	nr_sequencia = nr_seq_deferimento_w;

delete from pls_analise_solicitacao
where	nr_sequencia = nr_seq_solicitacao_w;

delete  from pls_analise_ocorrencia a
where	exists (SELECT	1
		from	pls_analise_adesao x
		where	x.nr_sequencia = a.nr_seq_analise
		and	x.nr_seq_pessoa_proposta = nr_seq_proposta_benef_w);

delete from pls_analise_adesao
where	nr_seq_pessoa_proposta = nr_seq_proposta_benef_w;

select	max(nr_sequencia)
into STRICT	nr_seq_declaracao_w
from	pls_declaracao_segurado
where	cd_pessoa_fisica = cd_beneficiario_p 
and	nr_seq_proposta_adesao = nr_sequencia_p;

delete from pls_declaracao_resposta
where	nr_seq_declaracao = nr_seq_declaracao_w;

delete from pls_declaracao_segurado
where	cd_pessoa_fisica = cd_beneficiario_p 
and	nr_seq_proposta_adesao = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
