CREATE OR REPLACE PROCEDURE pls_propos_mig_gera_benef_list ( nr_seq_proposta_p bigint, nr_seq_beneficiario_lista_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_beneficiario_lista_w	varchar(2000);
tam_lista_w		smallint;
ie_pos_virgula_w		smallint;
nr_seq_beneficiario_w	bigint;


BEGIN 
 
nr_seq_beneficiario_lista_w	:= nr_seq_beneficiario_lista_p;
 
while(nr_seq_beneficiario_lista_w IS NOT NULL AND nr_seq_beneficiario_lista_w::text <> '') loop 
	begin 
	tam_lista_w	:= length(nr_seq_beneficiario_lista_w);
	ie_pos_virgula_w	:= position(',' in nr_seq_beneficiario_lista_w);
	 
	if (ie_pos_virgula_w <> 0) then 
		begin 
		nr_seq_beneficiario_w	:= substr(nr_seq_beneficiario_lista_w, 1, (ie_pos_virgula_w - 1));
		nr_seq_beneficiario_lista_w	:= substr(nr_seq_beneficiario_lista_w, (ie_pos_virgula_w + 1), tam_lista_w);
		 
		CALL pls_proposta_mig_gerar_benef( 
			nr_seq_proposta_p, 
			nr_seq_beneficiario_w, 
			nm_usuario_p);
		end;
	end if;
	end;
end loop;
 
end;
