CREATE OR REPLACE PROCEDURE pls_prop_alt_dt_prev_entrega ( nr_seq_proposta_p bigint, dt_prev_entrega_contrato_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_prev_entrega_contrato_w	timestamp;
ds_historico_w		varchar(255);


BEGIN

select	dt_prev_entrega_contrato
into STRICT	dt_prev_entrega_contrato_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;


update	pls_proposta_adesao
set	dt_prev_entrega_contrato	= dt_prev_entrega_contrato_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_proposta_p;

if (dt_prev_entrega_contrato_w IS NOT NULL AND dt_prev_entrega_contrato_w::text <> '') then
	ds_historico_w	:= 'Data prevista de entrega da proposta alterada de '|| to_char(dt_prev_entrega_contrato_w, 'DD/MM/YYYY HH24:MI')||' para '||to_char(dt_prev_entrega_contrato_p, 'DD/MM/YYYY HH24:MI');
else
	ds_historico_w	:= 'Data prevista de entrega da proposta alterada de EM BRANCO para '||to_char(dt_prev_entrega_contrato_p, 'DD/MM/YYYY HH24:MI');
end if;

insert	into	pls_proposta_historico(	nr_sequencia, nr_seq_proposta, dt_historico,
		ds_historico, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, ie_origem_historico,
		dt_liberacao_historico,nm_usuario_liberacao)
	values (	nextval('pls_proposta_historico_seq'), nr_seq_proposta_p, clock_timestamp(),
		ds_historico_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, 1, clock_timestamp(), nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
