SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_prorrogar_venc_carteira ( nr_seq_lote_p bigint, ie_contrato_p text, qt_dias_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_segurado_w		bigint;
nr_contrato_w			bigint;
dt_validade_carteira_w		timestamp;
dt_validade_cart_nova_w		timestamp;
nr_seq_seg_carteira_w		bigint;
nr_seq_carteira_venc_w		bigint;
qt_dias_valid_cart_w		bigint;
ie_venc_cart_fim_mes_w		varchar(10);
dt_rescisao_programada_w	timestamp;
dt_rescisao_w			timestamp;
ie_venc_cartao_rescisao_w	pls_parametros.ie_venc_cartao_rescisao%type;

C01 CURSOR FOR 
	SELECT	b.nr_seq_segurado, 
		(substr(pls_obter_dados_segurado(b.nr_seq_segurado,'CC'),1,50))::numeric , 
		a.nr_seq_seg_carteira, 
		a.nr_sequencia, 
		a.DT_VALIDADE_PRORROGADA, 
		c.dt_rescisao 
	from	pls_carteira_vencimento a, 
		pls_segurado_carteira	b, 
		pls_segurado		c 
	where	a.nr_seq_seg_carteira	= b.nr_sequencia 
	and	b.nr_seq_segurado	= c.nr_sequencia 
	and	nr_seq_lote		= nr_seq_lote_p;
	

BEGIN 
 
qt_dias_valid_cart_w	:= qt_dias_p;
 
select	ie_venc_cart_fim_mes, 
	ie_venc_cartao_rescisao 
into STRICT	ie_venc_cart_fim_mes_w, 
	ie_venc_cartao_rescisao_w 
from	pls_parametros 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_segurado_w, 
	nr_contrato_w, 
	nr_seq_seg_carteira_w, 
	nr_seq_carteira_venc_w, 
	dt_validade_carteira_w, 
	dt_rescisao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	dt_rescisao_programada_w	:= null;
	 
	if (ie_contrato_p	= 'S') then 
		begin 
		qt_dias_valid_cart_w	:= pls_obter_meses_renovacao(nr_seq_segurado_w);
		exception 
			when no_data_found then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(187963,'nr_contrato='||nr_contrato_w);
		end;
	end if;
	 
	if (coalesce(dt_validade_carteira_w::text, '') = '') then 
		select	dt_validade_carteira 
		into STRICT	dt_validade_carteira_w 
		from	pls_segurado_carteira 
		where	nr_sequencia	= nr_seq_seg_carteira_w;
	end if;
	 
	dt_validade_cart_nova_w := (dt_validade_carteira_w + qt_dias_valid_cart_w);
	 
	/*aaschlote 08/08/2012 OS - 475468*/
 
	if (ie_venc_cart_fim_mes_w = 'S') then 
		dt_validade_cart_nova_w	:= last_day(dt_validade_cart_nova_w);
	end if;
	 
	begin 
	dt_rescisao_programada_w	:= pls_obter_data_rescisao_seg(nr_seq_segurado_w, ie_venc_cartao_rescisao_w);
	exception 
	when others then 
		dt_rescisao_programada_w	:= null;
	end;
	 
	if (coalesce(dt_rescisao_programada_w::text, '') = '') then 
		dt_rescisao_programada_w := dt_rescisao_w;
	end if;
	 
	/*aaschlote 14/05/2012 OS - 445193*/
 
	/*aaschlote 28/01/2013 OS - 537786 - Caso a data da validade prorrogada seja maior que a rescisão programada, então a validade será a rescisão programada*/
 
	if (dt_rescisao_programada_w IS NOT NULL AND dt_rescisao_programada_w::text <> '') and (dt_rescisao_programada_w	< dt_validade_cart_nova_w) then 
		dt_validade_cart_nova_w	:= dt_rescisao_programada_w;
	end if;
	 
	/*aaschlote 30/04/2012 OS - 423598 - Irá alterar a validade no cartão quando colocar o lote de vencimento para definitivo*/
 
	update	pls_carteira_vencimento 
	set	DT_VALIDADE_PRORROGADA	= dt_validade_cart_nova_w, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia		= nr_seq_carteira_venc_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
