CREATE OR REPLACE PROCEDURE pls_protocolo_conta_beforepost ( ie_origem_p text, ie_tipo_guia_p text, nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, nr_seq_protocolo_p bigint, dt_protocolo_p timestamp, dt_mes_competencia_p timestamp, cd_condicao_pagamento_p INOUT bigint, ie_tipo_resp_credito_p INOUT text, ie_forma_pagamento_p INOUT text, ds_erro_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				 
cd_condicao_pagamento_w	bigint;
ie_tipo_resp_credito_w	varchar(10);
ie_forma_pagamento_w	varchar(10);
ds_erro_w		varchar(80) := '';
qt_segurado_w		bigint;


BEGIN 
 
select	count(1) 
into STRICT	qt_segurado_w 
from	pls_segurado 
where	nr_sequencia = nr_seq_segurado_p;
 
/* beneficiário é obrigatório e dava erro quando usuário digitava seq de beneficiário não existente */
 
if (qt_segurado_w > 0) then 
 
	cd_condicao_pagamento_w := cd_condicao_pagamento_p;
	ie_tipo_resp_credito_w	:= ie_tipo_resp_credito_p;
	ie_forma_pagamento_w	:= ie_forma_pagamento_p;
 
	SELECT * FROM pls_obter_regra_atrib_prot(ie_origem_p, ie_tipo_guia_p, nr_seq_segurado_p, nm_usuario_p, cd_condicao_pagamento_w, ie_tipo_resp_credito_w, ie_forma_pagamento_w) INTO STRICT cd_condicao_pagamento_w, ie_tipo_resp_credito_w, ie_forma_pagamento_w;
 
	cd_condicao_pagamento_p := cd_condicao_pagamento_w;
	ie_tipo_resp_credito_p := ie_tipo_resp_credito_w;
	ie_forma_pagamento_p := ie_forma_pagamento_w;
 
	if (ie_tipo_resp_credito_w = 'PI') and (coalesce(cd_pessoa_fisica_p::text, '') = '') then 
		ds_erro_w := obter_texto_dic_objeto(126773, wheb_usuario_pck.get_nr_seq_idioma, null);
	end if;
 
 
	if (ds_erro_w <> '') then 
		CALL pls_consistir_protocolo_reemb(nr_seq_protocolo_p, dt_protocolo_p, dt_mes_competencia_p, cd_estabelecimento_p, nm_usuario_p);
	end if;
end if;
 
ds_erro_p := ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
