CREATE OR REPLACE PROCEDURE pls_ptu_baca_a700_seq_plano () AS $body$
DECLARE

 
c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		pls_obter_dados_segurado(a.nr_seq_segurado, 'NRP') nr_seq_plano 
	from	pls_conta		a, 
		pls_protocolo_conta	b 
	where	b.nr_sequencia		= a.nr_seq_protocolo 
	and	b.ie_origem_protocolo	= 'Z' -- importação A700 
	and	b.ie_situacao		in ('D', 'T') 
	and	a.ie_origem_conta	= 'Z' -- importação A700 
	and	coalesce(a.nr_seq_plano::text, '') = '';

nr_sequencia_w	dbms_sql.number_table;
nr_seq_plano_w	dbms_sql.number_table;

i		integer;


BEGIN 
 
open c01;
loop 
	fetch c01 bulk collect into	nr_sequencia_w, 
					nr_seq_plano_w 
	limit	pls_util_pck.qt_registro_transacao_w;
	exit when nr_sequencia_w.count = 0;
	 
	forall i in nr_sequencia_w.first..nr_sequencia_w.last 
		update	pls_conta 
		set	nr_seq_plano	= nr_seq_plano_w(i) 
		where	nr_sequencia	= nr_sequencia_w(i);
	 
	commit;
	 
	nr_sequencia_w.delete;
	nr_seq_plano_w.delete;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ptu_baca_a700_seq_plano () FROM PUBLIC;

