CREATE OR REPLACE PROCEDURE pls_qt_contas_consistir ( nr_seq_ocorrencia_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text, retorno_p INOUT text) AS $body$
DECLARE


nr_seq_conta_w			bigint;
qt_conta_w			bigint:= 0;
ds_retorno_w			varchar(255);
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;


BEGIN


if (ie_opcao_p = 'C' ) then
	begin
	select 	count(nr_sequencia)
	into STRICT	qt_conta_w
	from 	pls_conta
	where   ie_status in ('A','P');
	end;

elsif (ie_opcao_p = 'P') then
	begin
	select 	count(a.nr_sequencia)
	into STRICT	qt_conta_w
	from 	pls_conta	b,
		pls_conta_proc	a
	where   b.ie_status in ('A','P')
	and	b.nr_sequencia = a.nr_seq_conta;
	end;

elsif (ie_opcao_p = 'M') then
	begin
	select 	count(a.nr_sequencia)
	into STRICT	qt_conta_w
	from 	pls_conta	b,
		pls_conta_mat	a
	where   b.ie_status in ('A','P')
	and	b.nr_sequencia = a.nr_seq_conta;
	end;
elsif (ie_opcao_p = 'F') then
	select	count(1)
	into STRICT	qt_conta_w
	from	pls_analise_conta a,
		pls_conta_pos_estabelecido b,
		pls_conta c
	where	a.nr_sequencia	= b.nr_seq_analise
	and	b.nr_seq_conta	= c.nr_sequencia
	and	a.ie_origem_analise = 2
	and	((a.ie_status in ('A', 'G'))
	or (a.ie_status = 'S') and exists (SELECT	1
						 from	pls_analise_conta x
						 where	x.nr_sequencia = a.nr_seq_analise_ref
						 and	x.ie_status = 'T'));
end if;
retorno_p:='Existem '||qt_conta_w||' itens a serem consistidos, deseja continuar? ';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
