SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_reajustar_regra_mens ( nr_seq_reajuste_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_regra_vl_min_w		bigint;
nr_seq_regra_mens_w		bigint;
vl_minimo_w			double precision;
tx_reajuste_w			double precision;
vl_minimo_novo_w		double precision;
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		vl_minimo
	from	pls_regra_mens_contrato
	where	nr_seq_contrato	= nr_seq_contrato_w
	and	ie_tipo_regra	= 'VM'
	and	coalesce(dt_fim_vigencia,clock_timestamp()) >= clock_timestamp()
	
union all

	SELECT	nr_sequencia,
		vl_minimo
	from	pls_regra_mens_contrato
	where	nr_seq_intercambio	= nr_seq_intercambio_w
	and	ie_tipo_regra	= 'VM'
	and	coalesce(dt_fim_vigencia,clock_timestamp()) >= clock_timestamp();


BEGIN

select	max(nr_seq_contrato),
	max(nr_seq_intercambio)
into STRICT	nr_seq_contrato_w,
	nr_seq_intercambio_w
from	pls_reajuste
where	nr_sequencia	= nr_seq_reajuste_p;

select	count(*)
into STRICT	qt_regra_vl_min_w
from	pls_regra_mens_contrato
where	((nr_seq_contrato	= nr_seq_contrato_w) or (nr_seq_intercambio = nr_seq_intercambio_w))
and	ie_tipo_regra		= 'VM'
and	coalesce(vl_minimo,0)	> 0;

if (qt_regra_vl_min_w > 0) and (coalesce(nr_seq_contrato_w,0) > 0) then
	select	tx_reajuste
	into STRICT	tx_reajuste_w
	from	pls_reajuste
	where	nr_sequencia	= nr_seq_reajuste_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_mens_w,
		vl_minimo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		vl_minimo_novo_w	:= round(vl_minimo_w + (vl_minimo_w * tx_reajuste_w) / 100,2);

		insert into pls_regra_mens_contrato(	nr_sequencia, vl_minimo, cd_estabelecimento, dt_inicio_vigencia,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
							dt_fim_vigencia, nr_seq_contrato, dt_limite_movimentacao,
							ie_tipo_data_limite, qt_interv_mes_mensalidade, ie_mensalidade_mes_rescisao, nr_seq_reajuste,
							ie_agrupar_valor, nr_seq_intercambio, vl_minimo_abat_fatura, pr_aplicacao_reajuste,
							nr_seq_regra_origem, ie_tipo_regra)
						(SELECT	nextval('pls_regra_mens_contrato_seq'), vl_minimo_novo_w, cd_estabelecimento, dt_inicio_vigencia,
							clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
							dt_fim_vigencia, nr_seq_contrato, dt_limite_movimentacao,
							ie_tipo_data_limite, qt_interv_mes_mensalidade, ie_mensalidade_mes_rescisao, nr_seq_reajuste_p,
							ie_agrupar_valor, nr_seq_intercambio, vl_minimo_abat_fatura, tx_reajuste_w,
							nr_seq_regra_mens_w, ie_tipo_regra
						from	pls_regra_mens_contrato
						where	nr_sequencia	= nr_seq_regra_mens_w);

		update	pls_regra_mens_contrato
		set	dt_fim_vigencia	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_regra_mens_w;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
