CREATE OR REPLACE PROCEDURE pls_reajusta_vl_min_regra_mens ( nr_seq_regra_p bigint, pr_reajuste_p bigint, dt_reajuste_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

insert	into	pls_regra_mens_contrato(	nr_sequencia,
		vl_minimo,
		cd_estabelecimento,
		dt_inicio_vigencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_limite_movimentacao,
		ie_tipo_data_limite,
		qt_interv_mes_mensalidade,
		ie_mensalidade_mes_rescisao,
		ie_agrupar_valor,
		nr_seq_contrato,
		nr_seq_intercambio,
		ie_pessoa_contrato,
		vl_minimo_abat_fatura,
		pr_aplicacao_reajuste,
		nr_seq_regra_origem,
		ie_tipo_regra
		)
	SELECT	nextval('pls_regra_mens_contrato_seq'),
		vl_minimo + ((vl_minimo / 100) * pr_reajuste_p),
		cd_estabelecimento_p,
		dt_reajuste_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_limite_movimentacao,
		ie_tipo_data_limite,
		qt_interv_mes_mensalidade,
		ie_mensalidade_mes_rescisao,
		ie_agrupar_valor,
		nr_seq_contrato,
		nr_seq_intercambio,
		ie_pessoa_contrato,
		vl_minimo_abat_fatura,
		pr_reajuste_p,
		nr_seq_regra_p,
		ie_tipo_regra
	from	pls_regra_mens_contrato
	where	nr_sequencia	= nr_seq_regra_p;

update	pls_regra_mens_contrato
set	dt_fim_vigencia	= dt_reajuste_p
where	nr_sequencia	= nr_seq_regra_p;

commit;

end;
$body$
