SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_realizar_hist_prog ( nr_seq_hist_cliente_p bigint, nr_seq_hist_solic_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_seq_hist_cliente_p,0) <> 0) then
	update	pls_comercial_historico
	set	dt_realizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_hist_cliente_p;
elsif (coalesce(nr_seq_hist_solic_p,0) <> 0) then
	update	pls_solicitacao_historico
	set	dt_realizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_hist_solic_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
