CREATE OR REPLACE PROCEDURE pls_realiza_manutenc_preco_js ( nr_seq_preco_lista_p text, tx_acrescimo_p bigint, tx_desconto_p bigint, ie_criar_lote_log_p text, nr_seq_motivo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_preco_lista_w	varchar(2000);
nr_seq_preco_w		bigint;
ie_criar_lote_log_w		varchar(1);
ds_erro_w			varchar(255);


BEGIN
ds_erro_p			:= '';
ie_criar_lote_log_w		:= ie_criar_lote_log_p;
nr_seq_preco_lista_w	:= nr_seq_preco_lista_p;
while (nr_seq_preco_lista_w IS NOT NULL AND nr_seq_preco_lista_w::text <> '') loop
	begin
	nr_seq_preco_w		:= substr(nr_seq_preco_lista_w, 1, position(',' in nr_seq_preco_lista_w) - 1);
	nr_seq_preco_lista_w	:= substr(nr_seq_preco_lista_w, position(',' in nr_seq_preco_lista_w) + 1, length(nr_seq_preco_lista_w));

	ds_erro_w := pls_realizar_manutencao_preco(
		nr_seq_preco_w, tx_acrescimo_p, tx_desconto_p, ie_criar_lote_log_w, nr_seq_motivo_p, nm_usuario_p, cd_estabelecimento_p, ds_erro_w);

	ie_criar_lote_log_w	:= 'N';

	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		begin
		if (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') then
			ds_erro_p	:= ds_erro_p || '#' || ds_erro_w;
		else
			ds_erro_p	:= ds_erro_w;
		end if;
		end;
	end if;
	end;
end loop;

end;
$body$
