CREATE OR REPLACE PROCEDURE pls_reativar_intercambio ( nr_seq_intercambio_p bigint, dt_reativacao_p timestamp, ie_dependente_p text, ds_observacao_p text, cd_estabelecimento_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
dt_rescisao_w			timestamp;
dt_exclusao_w			timestamp;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	(dt_rescisao IS NOT NULL AND dt_rescisao::text <> '')
	and	coalesce(nr_seq_titular::text, '') = '';


BEGIN


select	dt_exclusao
into STRICT	dt_exclusao_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

/*Reativa os beneficiários que foram rescindidos no mesmo dia do contrato*/

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	trunc(dt_rescisao,'dd')
	into STRICT	dt_rescisao_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_w;

	if (trunc(dt_exclusao_w,'dd') = dt_rescisao_w) then
		CALL pls_reativar_seg_intercambio(nr_seq_segurado_w,dt_reativacao_p,ds_observacao_p,'C','N',ie_dependente_p,null,cd_estabelecimento_p,nm_usuario_p);
	end if;

	end;
end loop;
close C01;

update	pls_intercambio
set	dt_exclusao		 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	dt_reativacao		= dt_reativacao_p
where	nr_sequencia		= nr_seq_intercambio_p;

insert into pls_contrato_historico(nr_sequencia,
			cd_estabelecimento,
			nr_seq_intercambio,
			dt_historico,
			ie_tipo_historico,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_historico)
		values (	nextval('pls_contrato_historico_seq'),
			cd_estabelecimento_p,
			nr_seq_intercambio_p,
			dt_reativacao_p,
			'2',
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'Reativação do contrato');

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
