CREATE OR REPLACE PROCEDURE pls_reativar_sca_beneficiario ( nr_seq_sca_vinculo_p bigint, nr_seq_segurado_p bigint, dt_reativacao_p timestamp, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


ds_plano_sca_w		varchar(255);
dt_fim_vigencia_w	timestamp;


BEGIN

select	max(dt_fim_vigencia)
into STRICT	dt_fim_vigencia_w
from	pls_sca_vinculo
where	nr_sequencia	= nr_seq_sca_vinculo_p;

ds_plano_sca_w	:= substr(pls_obter_dados_vinc_sca(nr_seq_sca_vinculo_p,'N'),1,225);

update	pls_sca_vinculo
set	dt_fim_vigencia	 = NULL,
	dt_reativacao	= dt_reativacao_p
where	nr_sequencia	= nr_seq_sca_vinculo_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '42', clock_timestamp(), 'Reativação do SCA '||ds_plano_sca_w ||'. Data rescisão anterior: ' ||dt_fim_vigencia_w,
				'pls_reativar_sca_beneficiario', null, null, null,
				null, null, null, null,
				null, null, nr_seq_sca_vinculo_p, null,
				nm_usuario_p, 'N');


if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
