SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recalcular_analise_conta ( nr_seq_resumo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_tipo_item_w			varchar(1);
ie_tipo_despesa_w		varchar(1);
nr_seq_item_w			bigint;
vl_liberado_w			double precision;


BEGIN 
 
 
select	ie_tipo_item, 
	ie_tipo_despesa, 
	nr_seq_item 
into STRICT	ie_tipo_item_w, 
	ie_tipo_despesa_w, 
	nr_seq_item_w 
from	w_pls_resumo_conta 
where	nr_sequencia = nr_seq_resumo_p;
 
if (ie_tipo_item_w = 'P') then 
	if (ie_tipo_despesa_w = '1') then /* Atualizar o valor do procedimento */
 
		CALL pls_atualiza_valor_proc(nr_seq_item_w, 'N', nm_usuario_p,'S',null,null);
	elsif (ie_tipo_despesa_w in ('2','3')) then /* Atualizar os valores das taxas e di√°rias */
 
		CALL pls_atualiza_valor_servico(nr_seq_item_w, 'N', nm_usuario_p,'S');
	elsif (ie_tipo_despesa_w = '4') then /* Atualizar os valores dos pacotes */
 
		CALL pls_atualiza_valor_pacote(nr_seq_item_w, 'C', nm_usuario_p, 'S', 'N');
	end if;	
elsif (ie_tipo_item_w = 'M') then 
	CALL pls_atualiza_valor_mat(nr_seq_item_w, 'N', nm_usuario_p,null,null);
end if;
 
if (ie_tipo_item_w = 'P') then 
	select	vl_procedimento 
	into STRICT	vl_liberado_w	 
	from	pls_conta_proc 
	where	nr_sequencia	= nr_seq_item_w;
elsif (ie_tipo_item_w = 'M') then 
	select	vl_material 
	into STRICT	vl_liberado_w	 
	from	pls_conta_mat 
	where	nr_sequencia	= nr_seq_item_w;
end if;
	 
update	w_pls_resumo_conta 
set	vl_total	= vl_liberado_w 
where	nr_sequencia	= nr_seq_resumo_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
