CREATE OR REPLACE PROCEDURE pls_recalcular_contas_fatura ( nr_seq_fatura_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Recalcular todos os itens da fatura A500 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [X] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ 
 
nr_seq_conta_w		bigint;
nr_seq_conta_proc_w	bigint;
nr_seq_conta_mat_w	bigint;

C01 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from	pls_conta	b, 
		pls_analise_conta a 
	where	b.nr_seq_analise	= a.nr_sequencia 
	and	b.ie_status		<> 'C' 
	and	b.nr_seq_fatura		= nr_seq_fatura_p;	
	 
C02 CURSOR FOR 
	SELECT	nr_sequencia, 
		null 
	from	pls_conta_proc 
	where	nr_seq_conta = nr_seq_conta_w 
	
union
 
	SELECT	null, 
		nr_sequencia 
	from	pls_conta_mat 
	where	nr_seq_conta = nr_seq_conta_w;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	-- Recalcular itens 
	open C02;
	loop 
	fetch C02 into	 
		nr_seq_conta_proc_w, 
		nr_seq_conta_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL pls_recalcular_item_log(nr_seq_conta_proc_w,nr_seq_conta_mat_w,'N',nm_usuario_p);
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
