SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recalcular_item_log ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_proc_mat_w		varchar(1);
ie_tipo_despesa_w	bigint;
nr_seq_conta_w		bigint;


BEGIN 
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then 
	begin 
		select	ie_tipo_despesa, 
			nr_seq_conta 
		into STRICT	ie_tipo_despesa_w, 
			nr_seq_conta_w 
		from	pls_conta_proc 
		where	nr_sequencia = nr_seq_conta_proc_p;
	exception 
	when others then 
		ie_tipo_despesa_w := null;
	end;
	 
	if (ie_tipo_despesa_w = '1') then /* Atualizar o valor do procedimento */
 
        CALL pls_atualiza_valor_proc(nr_seq_conta_proc_p, 'S', nm_usuario_p,'S','C',null);
    elsif (ie_tipo_despesa_w in ('2','3')) then /* Atualizar os valores das taxas e diárias */
 
        CALL pls_atualiza_valor_servico(nr_seq_conta_proc_p, 'S', nm_usuario_p,'N');
    elsif (ie_tipo_despesa_w = '4') then 
        CALL pls_atualiza_valor_pacote(nr_seq_conta_proc_p, 'C', nm_usuario_p, 'N', 'S');/* Atualizar os valores dos*/
    else 
        CALL wheb_mensagem_pck.exibir_mensagem_abort(182981); --'Problemas com a geração de valores. Tipo de de 
    end if;
 
	/* Verifica a aplicação da regra 1705 1706 1707 e 9919*/
 
	CALL pls_obter_regra_valor_conta(	nr_seq_conta_w,	nr_seq_conta_proc_p,null,null,nm_usuario_p);
	/* Liberação automática */
 
	CALL pls_liberar_item_automatic(nr_seq_conta_w,nr_seq_conta_proc_p,null,nm_usuario_p);
	/*Caso o item esteja liberado pelo usuário fazer a liberação proporcional*/
 
	CALL pls_atualiza_lib_conta(nr_seq_conta_w, 'C', nm_usuario_p);	
	/* Atualizar resumo */
 
	CALL pls_atualizar_item_resumo(nr_seq_conta_proc_p,'P',nm_usuario_p,'N');
	 
	 
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then 
	CALL pls_atualiza_valor_mat(nr_seq_conta_mat_p,'S',nm_usuario_p,'N',null);
	 
	select	nr_seq_conta 
	into STRICT	nr_seq_conta_w 
	from	pls_conta_mat 
	where	nr_sequencia = nr_seq_conta_mat_p;
	 
	/* Regra valor */
 
	CALL pls_obter_regra_valor_conta(nr_seq_conta_w, null, nr_seq_conta_mat_p,null,nm_usuario_p);
	 
	/* Liberação automática */
 
	CALL pls_liberar_item_automatic(nr_seq_conta_w,null,nr_seq_conta_mat_p,nm_usuario_p);
	 
	/*Caso o item esteja liberado pelo usuário fazer a liberação proporcional*/
 
	CALL pls_atualiza_lib_conta(nr_seq_conta_w, 'C', nm_usuario_p);
	 
	/* Atualizar resumo */
 
	CALL pls_atualizar_item_resumo(nr_seq_conta_mat_p,'M',nm_usuario_p,'N');
		 
end if;
 
if (coalesce(ie_commit_p,'N') = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_recalcular_item_log ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

