CREATE OR REPLACE PROCEDURE pls_recalcular_mens_pag ( nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_mensalidade_w	pls_mensalidade.nr_sequencia%type;
dt_mesano_referencia_w	pls_lote_mensalidade.dt_mesano_referencia%type;
nr_seq_solic_resc_fin_w	pls_solic_rescisao_fin.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_mensalidade,
		nr_seq_pagador
	from	pls_mensalidade
	where	nr_seq_lote	= nr_seq_lote_p
	and (nr_sequencia	= nr_seq_mensalidade_p or coalesce(nr_seq_mensalidade_p,0) = 0)
	and	coalesce(ie_cancelamento::text, '') = '';

BEGIN

select	dt_mesano_referencia,
	nr_seq_solic_resc_fin
into STRICT	dt_mesano_referencia_w,
	nr_seq_solic_resc_fin_w
from	pls_lote_mensalidade
where	nr_sequencia	= nr_seq_lote_p;

CALL pls_mensalidade_util_pck.carregar_parametros(cd_estabelecimento_p);
CALL pls_mensalidade_util_pck.carregar_regras_ops(cd_estabelecimento_p, nm_usuario_p, dt_mesano_referencia_w, null, nr_seq_solic_resc_fin_w);

for r_c01_w in C01 loop
	begin
	CALL pls_deletar_mensalidade(r_c01_w.nr_seq_mensalidade, 'N', nm_usuario_p, 'N');
	
	CALL pls_mens_selecao_benef_pck.selecionar_benef_pagador(nr_seq_lote_p, r_c01_w.nr_seq_pagador, cd_estabelecimento_p, nm_usuario_p);
	pls_mens_beneficiario_pck.inserir_mensalidade_benef(nr_seq_lote_p, r_c01_w.nr_seq_mensalidade, cd_estabelecimento_p, nm_usuario_p);
	CALL pls_mens_itens_pck.gerar_itens_mensalidade(nr_seq_lote_p, r_c01_w.nr_seq_mensalidade, cd_estabelecimento_p, nm_usuario_p);
	CALL pls_mens_processo_pck.processar_mensalidade(nr_seq_lote_p, r_c01_w.nr_seq_mensalidade, dt_mesano_referencia_w, cd_estabelecimento_p, nm_usuario_p);
	CALL pls_mens_processo_pck.processar_tributos(nr_seq_lote_p, r_c01_w.nr_seq_mensalidade, cd_estabelecimento_p, nm_usuario_p);
	end;
end loop;

CALL pls_mens_atualizacao_pck.atualizar_valores_lote(nr_seq_lote_p, nm_usuario_p, cd_estabelecimento_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
