SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recalcular_protocolo ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
vl_procedimentos_w		double precision	:= 0;
vl_diarias_w			double precision	:= 0;
vl_taxas_w			double precision	:= 0;
vl_materiais_w			double precision	:= 0;
vl_medicamentos_w		double precision	:= 0;
vl_gases_w			double precision	:= 0;
vl_opm_w			double precision	:= 0;
vl_cobrado_w			double precision	:= 0;
vl_total_w			double precision	:= 0;
vl_glosa_w			double precision	:= 0;
vl_saldo_w			double precision	:= 0;
nr_seq_conta_w			bigint;
nr_seq_segurado_w		bigint;
nr_seq_conta_proc_w		bigint;
ie_tipo_guia_w			varchar(2);
dt_autorizacao_w			timestamp;
vl_liberado_w			double precision;
ie_mes_fechado_w			varchar(1);
nr_seq_conta_mat_w		bigint;

nr_seq_plano_w			bigint;
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;
dt_contratacao_w		timestamp;
qt_idade_w			bigint;
ie_titularidade_w		varchar(2);
ie_status_w			pls_conta.ie_status%type;

c01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_segurado, 
		ie_tipo_guia, 
		coalesce(dt_atendimento_referencia, coalesce(dt_autorizacao,clock_timestamp())), 
		ie_status 
	from	pls_conta 
	where	nr_seq_protocolo	= nr_seq_protocolo_p;

c02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_conta_proc 
	where	nr_seq_conta	= nr_seq_conta_w 
	and	ie_status	in ('L','S');
	
c03 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_conta_mat 
	where	nr_seq_conta	= nr_seq_conta_w 
	and	ie_status	in ('L','S');


BEGIN 
 
if (coalesce(nr_seq_protocolo_p,0) <> 0) then 
	select	pls_obter_se_mes_fechado(dt_mes_competencia,'T',cd_estabelecimento_p) 
	into STRICT	ie_mes_fechado_w 
	from	pls_protocolo_conta 
	where	nr_sequencia	= nr_seq_protocolo_p;
	 
	if (ie_mes_fechado_w = 'S') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(191786,null);
	end if;
	 
	open c01;
	loop 
	fetch c01 into	 
		nr_seq_conta_w, 
		nr_seq_segurado_w, 
		ie_tipo_guia_w, 
		dt_autorizacao_w, 
		ie_status_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		--select	nr_seq_plano, 
		select	pls_obter_produto_benef(nr_sequencia,dt_autorizacao_w), 
			nr_seq_contrato, 
			nr_seq_intercambio, 
			dt_contratacao, 
			obter_idade_pf(cd_pessoa_fisica,clock_timestamp(),'A'), 
			CASE WHEN coalesce(nr_seq_titular::text, '') = '' THEN 'T'  ELSE 'D' END  
		into STRICT	nr_seq_plano_w, 
			nr_seq_contrato_w, 
			nr_seq_intercambio_w, 
			dt_contratacao_w, 
			qt_idade_w, 
			ie_titularidade_w 
		from	pls_segurado 
		where	nr_sequencia	= nr_seq_segurado_w;
		exception 
		when others then 
			nr_seq_contrato_w	:= 0;
			nr_seq_intercambio_w	:= 0;
			nr_seq_plano_w		:= 0;			
		end;
		 
		CALL pls_fechar_conta(nr_seq_conta_w, 'N', 'N', 'N', cd_estabelecimento_p, nm_usuario_p, null, null);
		 
		if (ie_status_w = 'F') then 
		 
			open c02;
			loop 
			fetch c02 into	nr_seq_conta_proc_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				CALL pls_gerar_coparticipacao(nr_seq_conta_proc_w, nr_seq_segurado_w, nr_seq_conta_w, 
							cd_estabelecimento_p, nm_usuario_p, null, 
							nr_seq_plano_w, nr_seq_contrato_w, nr_seq_intercambio_w, 
							dt_contratacao_w, qt_idade_w, ie_titularidade_w, 
							null,null,null, 
							null);
			end loop;
			close c02;
			 
			open C03;
			loop 
			fetch C03 into	nr_seq_conta_mat_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin 
				CALL pls_gerar_coparticipacao(null, nr_seq_segurado_w, nr_seq_conta_w, 
							cd_estabelecimento_p, nm_usuario_p, nr_seq_conta_mat_w, 
							nr_seq_plano_w, nr_seq_contrato_w, nr_seq_intercambio_w, 
							dt_contratacao_w, qt_idade_w, ie_titularidade_w, 
							null,null,null, 
							null);
				end;
			end loop;
			close C03;
			CALL pls_atualiza_status_copartic(nr_seq_conta_w, 'FC', null, nm_usuario_p, cd_estabelecimento_p);
		end if;
	end loop;
	close c01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_recalcular_protocolo ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

