SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recalcular_pro_rata_mes ( dt_mes_p timestamp, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE

 
tb_nr_seq_mensalidade_w		pls_util_cta_pck.t_number_table;
					
C01 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from	pls_mensalidade 	b, 
		pls_lote_mensalidade	a 
	where	b.dt_referencia 	= dt_mes_p 
	and	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	a.ie_status		= '2' 
	and	a.nr_sequencia		= b.nr_seq_lote 
	and	coalesce(b.ie_cancelamento::text, '') = '';
			
BEGIN 
open C01;
loop 
	tb_nr_seq_mensalidade_w.delete;
fetch C01 bulk collect into tb_nr_seq_mensalidade_w limit 1000;
exit when tb_nr_seq_mensalidade_w.count = 0;
	for i in tb_nr_seq_mensalidade_w.first..tb_nr_seq_mensalidade_w.last loop 
		CALL pls_gerar_pro_rata_dia(tb_nr_seq_mensalidade_w(i));
	end loop;	
	 
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
