SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recalcular_simulacao_web ( nr_seq_simulacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Recalcular a simulação de preço gerada pela Solicitação de Proposta on-line
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ x] Outros: HTML5
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_simulacao_w		pls_simulacao_preco.vl_simulacao%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_simulpreco_individual
	where	nr_seq_simulacao	= nr_seq_simulacao_p
	and	ie_tipo_benef <> 'R';

BEGIN

CALL pls_lanc_bonific_simul_online(nr_seq_simulacao_p, null, '1', nm_usuario_p);

for C01_w in C01 loop
	begin
	vl_simulacao_w	:= 0;

	select	vl_item
	into STRICT	vl_simulacao_w
	from	table(pls_simulacao_preco_pck.obter_resumo_simulacao(nr_seq_simulacao_p,null,null,null,'S'))
	where	nr_seq_simul_individual	= C01_w.nr_sequencia
	and	ie_totalizador = 'S';

	update	pls_simulpreco_individual
	set	vl_mensalidade		= vl_simulacao_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= C01_w.nr_sequencia;
	end;
end loop;

vl_simulacao_w	:= pls_simulacao_preco_pck.obter_valor_simulacao(nr_seq_simulacao_p,'I',null,null,null);

update	pls_simulacao_preco
set	vl_simulacao		= vl_simulacao_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_simulacao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_recalcular_simulacao_web ( nr_seq_simulacao_p bigint, nm_usuario_p text) FROM PUBLIC;

