CREATE OR REPLACE PROCEDURE pls_receber_notificacao ( nr_seq_notific_pagador_p bigint, dt_recebimento_p timestamp, nm_pessoa_recebimento_p text, nr_seq_vinculo_p bigint, nm_usuario_p text, ie_tipo_notificacao_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w			varchar(255)  := '';
dt_envio_lote_w			timestamp;
dt_limite_recebimento_w		timestamp;
ie_status_w			pls_notificacao_pagador.ie_status%type;


BEGIN
select	max(dt_envio),
	max(dt_limite_recebimento)
into STRICT	dt_envio_lote_w,
	dt_limite_recebimento_w
from	pls_notificacao_pagador	a,
	pls_notificacao_lote	b
where	b.nr_sequencia	= a.nr_seq_lote
and	a.nr_sequencia	= nr_seq_notific_pagador_p;

if (dt_limite_recebimento_w IS NOT NULL AND dt_limite_recebimento_w::text <> '') and (dt_recebimento_p > dt_limite_recebimento_w) then
	ie_status_w	:= 'F';
else
	ie_status_w	:= null;
end if;

if (coalesce(dt_envio_lote_w::text, '') = '') then
	ds_erro_w  := ds_erro_w || nr_seq_notific_pagador_p;
else
	update  pls_notificacao_pagador
	set	dt_recebimento_notif	= dt_recebimento_p,
		nm_pessoa_notific	= nm_pessoa_recebimento_p,
		nr_seq_vinculo_pagador	= CASE WHEN coalesce(nr_seq_vinculo_p, 0)=0 THEN  null  ELSE nr_seq_vinculo_p END ,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		ie_status_ant		= ie_status,
		ie_status		= coalesce(ie_status_w, CASE WHEN ie_status='C' THEN  'C' WHEN ie_status='P' THEN  'P'  ELSE 'R' END ),
		ie_tipo_notificacao	= ie_tipo_notificacao_p
	where	nr_sequencia		= nr_seq_notific_pagador_p;

	--Utilizado no HTML5
	CALL pls_excluir_barras_pag_notific(nr_seq_notific_pagador_p, nm_usuario_p);
end if;

ds_erro_p  := ds_erro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
