CREATE OR REPLACE PROCEDURE pls_receb_notif_proprio_pag ( nr_seq_notific_pagador_p bigint, ie_tipo_notificacao_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
