CREATE OR REPLACE PROCEDURE pls_reconhecer_ind_nv ( nr_seq_defesa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_impugnacao_w		bigint;
nr_seq_conta_w			bigint;
ie_status_conta_w		varchar(3);
ie_instancia_conta_w		varchar(3);
vl_pendente_w			double precision	:= 0;
ie_tipo_impugnacao_w		pls_processo_conta.ie_tipo_impugnacao%type;
vl_procedimento_w		pls_processo_procedimento.vl_procedimento%type;


BEGIN

select	b.nr_sequencia,
	b.nr_seq_conta,
	c.ie_status_conta,
	c.ie_instancia_conta,
	coalesce(c.ie_tipo_impugnacao,'C')
into STRICT	nr_seq_impugnacao_w,
	nr_seq_conta_w,
	ie_status_conta_w,
	ie_instancia_conta_w,
	ie_tipo_impugnacao_w
from	pls_formulario_defesa  a,
	pls_formulario b,
	pls_processo_conta c
where	a.nr_seq_formulario	= b.nr_sequencia
and	b.nr_seq_conta		= c.nr_sequencia
and	a.nr_sequencia		= nr_seq_defesa_p;

update	pls_formulario
set	ie_status_impugnacao	= 'F'
where	nr_sequencia	= nr_seq_impugnacao_w;

if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then
	select	coalesce(vl_pendente,0)
	into STRICT	vl_pendente_w
	from	pls_processo_conta
	where	nr_sequencia	= nr_seq_conta_w;
else
	vl_pendente_w := 0;
end if;

insert into pls_processo_alteracao(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_conta,
					ie_status_impugnacao,
					ie_status_conta,
					ie_instancia_conta,
					ie_status_pagamento,
					vl_deferido,
					vl_pendente,
					vl_ressarcir,
					ds_acao)
				values (	nextval('pls_processo_alteracao_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_conta_w,
					'F',
					ie_status_conta_w,
					ie_instancia_conta_w,
					'P',
					0,
					0,
					vl_pendente_w,
					'Reconhecer indeferimento');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
