SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_recusar_inco_proposta_list (nr_seq_validacao_lista_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Recusar todas as inconsistências da proposta de adesão, selecionadas no grid.

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_validacao_lista_w	varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		bigint;
nr_seq_validacao_w		bigint;

/* ie_opcao_p
'R' - Recusar inconsistência
'D' - Desfazer liberação */
BEGIN

nr_seq_validacao_lista_w	:= nr_seq_validacao_lista_p;

while(nr_seq_validacao_lista_w IS NOT NULL AND nr_seq_validacao_lista_w::text <> '') loop
	begin
	tam_lista_w	:= length(nr_seq_validacao_lista_w);
	ie_pos_virgula_w	:= position(',' in nr_seq_validacao_lista_w);

	if (ie_pos_virgula_w <> 0) then
		begin
		nr_seq_validacao_w		:= substr(nr_seq_validacao_lista_w, 1, (ie_pos_virgula_w - 1));
		nr_seq_validacao_lista_w	:= substr(nr_seq_validacao_lista_w, (ie_pos_virgula_w + 1), tam_lista_w);

		if (ie_opcao_p = 'R') then
			CALL pls_recusar_inco_proposta(nr_seq_validacao_w, nm_usuario_p);
		elsif (ie_opcao_p = 'D') then
			CALL pls_desfazer_lib_inco_proposta(nr_seq_validacao_w, nm_usuario_p);
		end if;
		end;
	end if;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
