SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_reemb_gerar_tipo_desp ( nr_sequencia_p bigint, ie_tipo_item_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_tipo_item
	P - Procedimentp
	M - Material
*/
ie_classificacao_w		varchar(1);
nr_seq_material_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;



BEGIN

if (ie_tipo_item_p	= 'P') then

	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	pls_reembolso_proc
	where	nr_sequencia	= nr_sequencia_p;

	select	ie_classificacao
	into STRICT	ie_classificacao_w
	from	procedimento
	where	cd_procedimento   = cd_procedimento_w
	and	ie_origem_proced  = ie_origem_proced_w;

	update pls_reembolso_proc
	set    ie_tipo_despesa   = ie_classificacao_w
	where  nr_sequencia      = nr_sequencia_p;

elsif (ie_tipo_item_p = 'M') then

	select	nr_seq_material
	into STRICT	nr_seq_material_w
	from	pls_reembolso_mat
	where	nr_sequencia	= nr_sequencia_p;

	select	ie_tipo_despesa
	into STRICT	ie_classificacao_w
	from	pls_material
	where	nr_sequencia = nr_seq_material_w;

	update	pls_reembolso_mat
	set	ie_tipo_despesa   = ie_classificacao_w
	where	nr_sequencia      = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
