CREATE OR REPLACE PROCEDURE pls_reenviar_sib_devolucao ( nr_seq_devolucao_p bigint, ie_tipo_devolucao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* ie_tipo_devolucao_p
	E = Erro
	R - Encerramento
	C - CCO
*/
BEGIN

if (ie_tipo_devolucao_p	= 'E') then
	update	sib_devolucao_erro
	set	ie_reenvio	= CASE WHEN ie_reenvio='S' THEN 'N' WHEN ie_reenvio='N' THEN 'S'  ELSE 'S' END
	where	nr_sequencia	= nr_seq_devolucao_p;
end if;

commit;

end;
$body$
