				elsif (ie_atributo_envio_w = 12) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ds_bairro_w || ''',';
				elsif (ie_atributo_envio_w = 13) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ds_municipio_w || ''',';
				elsif (ie_atributo_envio_w = 14) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_municipio_ibge_resid_w || ''',';
				elsif (ie_atributo_envio_w = 15) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_cep_w || ''',';
				elsif (ie_atributo_envio_w = 16) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ie_tipo_logradouro_w || ''',';
				elsif (ie_atributo_envio_w = 17) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ie_resid_brasil_w || ''',';
				elsif (ie_atributo_envio_w = 18) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_usuario_plano_w || ''',';
				elsif (ie_atributo_envio_w = 19) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_vinculo_benef_w || ''',';
				elsif (ie_atributo_envio_w = 20) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_usuario_plano_tit_w || ''',';
				elsif (ie_atributo_envio_w = 21) then
					ds_values_atributos_w := ds_values_atributos_w || 'to_date('''|| dt_liberacao_w || '''),';
				elsif (ie_atributo_envio_w = 22) then
					ds_values_atributos_w := ds_values_atributos_w || ''''||cd_plano_ans_w||''',';
				elsif (ie_atributo_envio_w = 23) then
					ds_values_atributos_w := ds_values_atributos_w || '''0'',';
				elsif (ie_atributo_envio_w = 24) then
					ds_values_atributos_w := ds_values_atributos_w || ''''||cd_plano_ans_pre_w||''',';
				elsif (ie_atributo_envio_w = 25) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ie_carencia_temp_w || ''',';
				elsif (ie_atributo_envio_w = 26) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || ie_itens_excluid_cobertura_w || ''',';
				elsif (ie_atributo_envio_w = 27) then	
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_cgc_estipulante_w|| ''',';
				elsif (ie_atributo_envio_w = 28) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || nr_matricula_cei_w|| ''',';
				elsif (ie_atributo_envio_w = 29) then
					ds_values_atributos_w := ds_values_atributos_w || 'to_date('''||dt_rescisao_w || '''),';
				elsif (ie_atributo_envio_w = 30) then
					ds_values_atributos_w := ds_values_atributos_w || '''' || cd_motivo_cancelamento_w|| ''',';
				elsif (ie_atributo_envio_w = 31) then
					ds_values_atributos_w := ds_values_atributos_w || 'to_date('''||dt_reinclusao_w || '''),';
				end if;		
				end;
			end loop;
			close C01;
		end if;
		
		if	((coalesce(nr_cco_w::text, '') = '') and (cd_cco_w IS NOT NULL AND cd_cco_w::text <> '')) then
			nr_cco_w	:= substr(cd_cco_w,1,10);
		end if;
		
		if	((coalesce(ie_digito_cco_w::text, '') = '') and (cd_cco_w IS NOT NULL AND cd_cco_w::text <> '')) then
			ie_digito_cco_w	:= substr(cd_cco_w,11,12);
		end if;
		
		ds_sql_w := ' insert into pls_interf_sib (nr_sequencia, nr_seq_lote_sib, dt_mesano_referencia, ie_tipo_reg, nr_cco, ie_digito_cco, nr_seq_segurado, nr_seq_titular, ';
		ds_sql_w := ds_sql_w || substr(ds_into_atributos_w, 1, length(ds_into_atributos_w) - 1) || ')';
		ds_sql_w := ds_sql_w || ' values	('||nr_seq_reg_arquivo_w||', ' || nr_seq_lote_sib_p || ', to_date('''||trunc(dt_mesano_referencia_w)||'''), '||ie_tipo_registro_w
					||', '||nr_cco_w||', '||ie_digito_cco_w||', '||nr_seq_segurado_w||', ';
		if (coalesce(nr_seq_titular_w,0) = 0) then
			ds_sql_w := ds_sql_w || 'null, ';
		else
			ds_sql_w := ds_sql_w || nr_seq_titular_w ||', ';
		end if;
		ds_sql_w := ds_sql_w || substr(ds_values_atributos_w, 1, length(ds_values_atributos_w) - 1) || ')';			
				
		CALL exec_sql_dinamico('INSERT INTO PLS_INTERF_SIB',ds_sql_w);
		
		if (ie_opcao_p in (1,2)) then
			update	pls_retorno_sib
			set	nr_seq_lote_envio	= nr_seq_lote_sib_p,
				nr_seq_interf_sib	= nr_seq_reg_arquivo_w
			where	nr_sequencia		= nr_seq_retorno_p;
		elsif (ie_opcao_p = 3) then
			update	pls_segurado
			set	ie_reenviar_sib		= 'N'
			where	nr_sequencia		= nr_seq_segurado_w;
			
			--Gravar na SIB log exclusao a informacao do reenvio
			if (nr_seq_sib_log_exclusao_w IS NOT NULL AND nr_seq_sib_log_exclusao_w::text <> '') then
				update	sib_log_exclusao
				set	ie_enviar_sib		= 'N',
					nr_seq_lote_envio	= nr_seq_lote_sib_p,
					nr_seq_interf_sib	= nr_seq_reg_arquivo_w
				where	nr_sequencia		= nr_seq_sib_log_exclusao_w;
			end if;
			
		elsif (ie_opcao_p = 4) then
			update	sib_devolucao_erro
			set	ie_reenvio		= 'N',
				nr_seq_lote_envio	= nr_seq_lote_sib_p,
				nr_seq_interf_sib	= nr_seq_reg_arquivo_w
			where	nr_sequencia		= nr_seq_retorno_p;
		end if;
		cd_plano_ans_pre_w	:= ' ';
	end if;
end if;
commit;

end;
$body$
