CREATE OR REPLACE PROCEDURE pls_registrar_receb_notif ( nr_seq_notificacao_p bigint, ds_responsavel_receb_p text, dt_recebimento_p timestamp, ie_tipo_notificacao_receb_p text, nr_seq_registro_cobr_p bigint default null) AS $body$
DECLARE


qt_registro_cobr_w	bigint;


BEGIN

select 	count(1)
into STRICT	qt_registro_cobr_w
from	pls_notificacao_receb
where 	nr_seq_registro_cobr	= nr_seq_registro_cobr_p
and 	nr_seq_notificacao 	= nr_seq_notificacao_p;

if (qt_registro_cobr_w = 0) then

	insert into pls_notificacao_receb(
		nr_sequencia,
		ds_responsavel_receb,
		dt_atualizacao,
		dt_recebimento,
		ie_tipo_notificacao_receb,
		nm_usuario,
		nr_seq_notificacao,
		nr_seq_registro_cobr
		)
	values (
		nextval('pls_notificacao_receb_seq'),
		ds_responsavel_receb_p,
		clock_timestamp(),
		dt_recebimento_p,
		ie_tipo_notificacao_receb_p,
		wheb_usuario_pck.get_nm_usuario,
		nr_seq_notificacao_p,
		nr_seq_registro_cobr_p
		);

	commit;
end if;

end;
$body$
