CREATE OR REPLACE PROCEDURE pls_regist_operador_disponivel ( nr_seq_operador_p bigint, ie_tipo_registro_p text, nm_usuario_p text, nr_ramal_p text, ie_pausa_central_p text) AS $body$
DECLARE


nr_seq_ausencia_w			bigint;

/*
ie_tipo_registro_p
E - Entrada
S - Sa√≠da
*/
BEGIN

if (ie_tipo_registro_p = 'E') then
	insert into pls_operador_controle(nr_sequencia, nr_seq_operador, dt_entrada,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, nr_ramal)
	values (nextval('pls_operador_controle_seq'), nr_seq_operador_p, clock_timestamp(),
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, nr_ramal_p);

	update	pls_atendimento_ausencia
	set	dt_fim	= clock_timestamp()
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	pls_atendimento_ausencia
				where	coalesce(dt_fim,to_date('01/01/1900','dd/mm/yyyy')) = to_date('01/01/1900','dd/mm/yyyy')
				and	nr_seq_operador = nr_seq_operador_p);

elsif (ie_tipo_registro_p = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_ausencia_w
	from	pls_atendimento_ausencia
	where	nr_seq_operador	= nr_seq_operador_p
	and	coalesce(dt_fim::text, '') = '';

	update	pls_operador_controle
	set	dt_saida		= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_pausa_central 	= ie_pausa_central_p,
		nr_seq_ausencia		= nr_seq_ausencia_w
	where	nr_sequencia 		= (	SELECT	max(nr_sequencia)
						from	pls_operador_controle
						where	nr_seq_operador = nr_seq_operador_p
						and	coalesce(dt_saida,to_date('01/01/1900','dd/mm/yyyy')) = to_date('01/01/1900','dd/mm/yyyy'));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
