SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_rejeitar_lote_imp_web (nr_seq_arquivo_p bigint, ds_erro_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_w		bigint;		
qt_analise_w		integer;
ds_log_call_w		plsprco_cta.ds_log_call%type;
ds_log_w		plsprco_cta.ds_log%type;
				
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_protocolo_conta
	where	nr_seq_lote_conta = nr_seq_lote_w;
		
BEGIN


select	max(nr_seq_lote)
into STRICT	nr_seq_lote_w
from	pls_xml_lote
where	nr_sequencia = nr_seq_arquivo_p;

if (nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then
	
	-- Tratamento para não rejeitar os protocolos que possuem conta em análise	
	select	count(1)
	into STRICT	qt_analise_w
	from	pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia		= b.nr_seq_protocolo
	and	a.nr_seq_lote_conta 	= nr_seq_lote_w
	and	(b.nr_seq_analise IS NOT NULL AND b.nr_seq_analise::text <> '');
	
	if (qt_analise_w = 0) then
		update	pls_lote_protocolo_conta
		set	dt_confirmacao  = NULL
		where	nr_sequencia = nr_seq_lote_w;

		update   pls_xml_lote
		set      ie_status = 'ERR', 
			 dt_finalizacao_confirmacao = clock_timestamp(), 
			 ds_erro = substr( ds_erro_p ,1,2000) 
		where    nr_sequencia = nr_seq_arquivo_p;

		for c_01 in C01 loop
			begin
				CALL pls_gerar_log_erro_importacao(c_01.nr_sequencia, nm_usuario_p, 5002,
							      ds_erro_p);
			
			end;
		end loop;
	else	
		for c_01 in C01 loop
			begin
			ds_log_call_w 	:= substr(pls_obter_detalhe_exec(false),1,1500);
			ds_log_w 	:= 'Rejeição do protocolo '||c_01.nr_sequencia||' não realizada, pois existem contas em análise.';
			
			insert into plsprco_cta(
							nr_sequencia, dt_atualizacao, nm_usuario,
							dt_atualizacao_nrec, nm_usuario_nrec, nm_tabela,
							ds_log, ds_log_call, ds_funcao_ativa, 
							ie_aplicacao_tasy, nm_maquina, ie_opcao,
							nr_seq_protocolo
						) values (
							nextval('plsprco_cta_seq'), clock_timestamp(), nm_usuario_p,
							clock_timestamp(), nm_usuario_p, 'PLS_PROTOCOLO_CONTA', 
							ds_log_w, ds_log_call_w, null, 
							'N', wheb_usuario_pck.get_machine, '0',
							c_01.nr_sequencia
						);			
			end;
		end loop;		
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
