CREATE OR REPLACE PROCEDURE pls_rejeitar_protocolo ( nr_seq_lote_p bigint, nr_seq_motivo_cancel_p bigint, ds_motivo_cancelamento_p text, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

			 
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_protocolo_conta 
	where	nr_seq_lote_conta = nr_seq_lote_p 
	order by coalesce(nr_seq_prot_referencia,0) desc;
	
BEGIN 
 
for r_c01_w in C01() loop 
	begin 
	CALL pls_cancelar_protocolo_conta(r_c01_w.nr_sequencia,'N',cd_estabelecimento_p, nm_usuario_p, ds_motivo_cancelamento_p, null);
	end;
end loop;
 
update	pls_lote_protocolo_conta 
set	ie_status 		= 'C', 
	nr_seq_motivo_cancel	= nr_seq_motivo_cancel_p, 
	ds_motivo_cancelamento	= ds_motivo_cancelamento_p 
where	nr_sequencia 		= nr_seq_lote_p;
 
commit;
 
insert into pls_log_lote_prot_conta(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, 
					nm_usuario_nrec, ds_log, nr_seq_lote_protocolo) 
		values (	nextval('pls_log_lote_prot_conta_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), 
					nm_usuario_p,' Cancelar lote',nr_seq_lote_p);
 
end;
$body$
