CREATE OR REPLACE PROCEDURE pls_rejeitar_recebimento_benef ( nr_seq_beneficiario_p ptu_intercambio_benef.nr_sequencia%type, nr_seq_benef_simp_p ptu_intercambio_benef_simp.nr_sequencia%type, nr_seq_motivo_rejeicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_w		bigint;
nm_beneficiario_w	varchar(250);


BEGIN

if (nr_seq_beneficiario_p IS NOT NULL AND nr_seq_beneficiario_p::text <> '') then
	select	substr(nm_beneficiario,1,255)
	into STRICT	nm_beneficiario_w
	from	ptu_intercambio_benef
	where	nr_sequencia = nr_seq_beneficiario_p;
	
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_segurado
	where	nr_seq_intercambio_benef	= nr_seq_beneficiario_p;
	
	if (qt_reg_w = 0) then
		update	ptu_intercambio_benef
		set	ie_status		=  'N',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario_cancelamento	= nm_usuario_p,
			dt_cancelamento		= clock_timestamp(),
			nr_seq_motivo_rejeicao	= nr_seq_motivo_rejeicao_p
		where	nr_sequencia		= nr_seq_beneficiario_p;
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(262483, 'NM_BENEFICIARIO=' || nm_beneficiario_w);
		/* Mensagem: Não é possível cancelar o beneficiário NM_BENEFICIARIO pois o mesmo já está em um contrato de intercâmbio! */

	end if;
elsif (nr_seq_benef_simp_p IS NOT NULL AND nr_seq_benef_simp_p::text <> '') then
	update	ptu_intercambio_benef_simp
	set	ie_status		=  'N',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario_cancelamento	= nm_usuario_p,
		dt_cancelamento		= clock_timestamp(),
		nr_seq_motivo_rejeicao	= nr_seq_motivo_rejeicao_p
	where	nr_sequencia		= nr_seq_benef_simp_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
