CREATE OR REPLACE PROCEDURE pls_rejeitar_reinclusao_benef ( nr_seq_reinclusao_p bigint, nr_seq_motivo_rejeicao_p bigint, ds_observacao_rejeicao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Rejeitar a solicitacao de reinclusao de beneficiario no tasy
