BEGIN
if (coalesce(nr_seq_reinclusao_p, 0) > 0) then
	update	pls_reinclusao_benef
	set	dt_rejeicao		= clock_timestamp(),
		ie_status		= 4,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_seq_motivo_rejeicao	= nr_seq_motivo_rejeicao_p,
		ds_observacao_rejeicao  = ds_observacao_rejeicao_p 
	where	nr_sequencia		= nr_seq_reinclusao_p
	and	coalesce(dt_confirmacao::text, '') = ''
	and	coalesce(dt_rejeicao::text, '') = '';
end if;

commit;

end;
$body$
