CREATE OR REPLACE PROCEDURE pls_rejeitar_solic_alt_cadast ( nr_seq_solicitacao_p bigint, nr_seq_motivo_rejeicao_p bigint, ds_observacao_rejeicao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_segurado_w 	bigint;


BEGIN 
 
if (coalesce(nr_seq_solicitacao_p,0) <> 0) then 
	update 	pls_segurado_solic_alt 
	set	dt_rejeicao	= clock_timestamp(), 
		nr_seq_motivo_rejeicao = nr_seq_motivo_rejeicao_p, 
		ds_observacao_rejeicao = ds_observacao_rejeicao_p 
	where 	nr_sequencia 	= nr_seq_solicitacao_p;	
end if;
 
select 	nr_seq_segurado 
into STRICT	nr_seq_segurado_w 
from 	pls_segurado_solic_alt 
where 	nr_sequencia =	nr_seq_solicitacao_p;
 
if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then 
	CALL pls_alerta_rejeicao_mov(nr_seq_segurado_w, null, null, 2, nr_seq_solicitacao_p, nm_usuario_p);
end if;
 
commit;
 
end;
$body$
