SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_rejeitar_solic_rescisao ( nr_seq_solicitacao_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			pls_solicitacao_rescisao.ie_status%type;
ie_retencao_w			pls_motivo_rejeicao_resc.ie_retencao%type;
ds_observacao_w			varchar(255);
nr_seq_protocolo_atend_w	pls_solicitacao_rescisao.nr_seq_protocolo_atend%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.vl_nota_credito,
		a.vl_saldo,
		a.ie_situacao
	from	nota_credito a,
		pls_solic_resc_fin_venc b,
		pls_solic_rescisao_fin c
	where	a.nr_sequencia	= b.nr_seq_nota_credito
	and	c.nr_sequencia	= b.nr_seq_solic_resc_fin
	and	c.nr_seq_solicitacao	= nr_seq_solicitacao_p
	and	a.vl_saldo > 0;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_solic_rescisao_fin
	where	nr_seq_solicitacao	= nr_seq_solicitacao_p
	and	ie_status in (1,2);

BEGIN

begin
select	nr_seq_protocolo_atend
into STRICT	nr_seq_protocolo_atend_w
from	pls_solicitacao_rescisao
where	nr_sequencia	= nr_seq_solicitacao_p;
exception
when others then
	nr_seq_protocolo_atend_w	:= null;
end;

for r_c01_w in C01 loop
	begin
	if (r_c01_w.vl_nota_credito = r_c01_w.vl_saldo) then
		CALL Cancelar_nota_credito(r_c01_w.nr_sequencia, clock_timestamp(), nm_usuario_p);
	elsif (r_c01_w.vl_nota_credito > r_c01_w.vl_saldo) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1097155,'NR_NOTA_CREDITO='||r_c01_w.nr_sequencia);--A nota de credito #@NR_NOTA_CREDITO#@ ja foi utilizada. E necessario analisar e cancelar a nota.
	end if;
	end;
end loop;


select	max(ie_retencao)
into STRICT	ie_retencao_w
from	pls_motivo_rejeicao_resc
where	nr_sequencia	= nr_seq_motivo_p;

if (ie_retencao_w = 'S') then
	ie_status_w	:= 4;
else
	ie_status_w	:= 5;
end if;

update	pls_solicitacao_rescisao
set	ie_status		= ie_status_w,
	nr_seq_motivo_rejeicao	= nr_seq_motivo_p,
	ds_observacao_rejeicao	= ds_observacao_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao 		= clock_timestamp()
where	nr_sequencia		= nr_seq_solicitacao_p;

if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	ds_observacao_w	:= chr(10) || wheb_mensagem_pck.get_texto(1120680) || ds_observacao_p;
else
	ds_observacao_w	:= null;
end if;

insert	into	pls_solicitacao_resc_hist(	nr_sequencia, nr_seq_solicitacao, dt_liberacao,
		nm_usuario, nm_usuario_nrec, dt_atualizacao, dt_atualizacao_nrec,
		ds_titulo, ds_historico)
	values (	nextval('pls_solicitacao_resc_hist_seq'), nr_seq_solicitacao_p, clock_timestamp(),
		nm_usuario_p, nm_usuario_p, clock_timestamp(), clock_timestamp(),
		wheb_mensagem_pck.get_texto(1120681), wheb_mensagem_pck.get_texto(1120682,'NR_SEQ_MOTIVO='||nr_seq_motivo_p||';DS_MOTIVO='||pls_obter_desc_motivo_rej_resc(nr_seq_motivo_p)||';DS_OBSERVACAO='||ds_observacao_w));

for r_c02_w in C02 loop
	begin
	CALL pls_solic_rescisao_fin_pck.cancelar_analise(r_c02_w.nr_sequencia, nm_usuario_p, cd_estabelecimento_p, 'N');
	end;
end loop;

CALL pls_alt_sit_atend_solic_resc(nr_seq_solicitacao_p, 'R', 'N', nm_usuario_p);

CALL pls_envio_email_solic_resc(nr_seq_solicitacao_p, 3, cd_estabelecimento_p, nm_usuario_p);

CALL pls_gerar_comunic_externo_web(nr_seq_solicitacao_p, '8', nm_usuario_p);

if (nr_seq_protocolo_atend_w IS NOT NULL AND nr_seq_protocolo_atend_w::text <> '') then
	update	pls_protocolo_atendimento
	set	ie_status	= 3,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_protocolo_atend_w;
	
	--Solicitacao de rescisao n #@NR_SEQ_SOLICITACAO#@ rejeitada.
	CALL pls_gravar_log_prot_atend_hist(nr_seq_protocolo_atend_w, wheb_mensagem_pck.get_texto(1120678,'NR_SEQ_SOLICITACAO='||nr_seq_solicitacao_p), nm_usuario_p, 'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_rejeitar_solic_rescisao ( nr_seq_solicitacao_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

