CREATE OR REPLACE PROCEDURE pls_remover_data_fim_pacote (nr_sequencia_p bigint) AS $body$
BEGIN
	update 	pls_pacote_tipo_acomodacao 
	set 	dt_fim_vigencia = '', 
		ie_situacao	= 'A'/* Rotina chamada no botão direito ativar composição no pacote, como solicitado na OS 940405 a rotina deve ativar a situação da regra também*/
 
	where 	nr_sequencia 	= nr_sequencia_p;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
