CREATE OR REPLACE PROCEDURE pls_remover_inclusao_lote ( nr_seq_inclusao_benef_p pls_inclusao_beneficiario.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nm_pessoa_fisica_w	pls_inclusao_beneficiario.nm_pessoa_fisica%type;
nr_seq_lote_inclusao_w	pls_inclusao_beneficiario.nr_seq_lote_inclusao%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_inclusao_beneficiario
	where	nr_seq_lote_inclusao = nr_seq_lote_inclusao_w
	and	nm_titular_inclusao = nm_pessoa_fisica_w
	
union

	SELECT	nr_sequencia
	from	pls_inclusao_beneficiario
	where	nr_seq_lote_inclusao = nr_seq_lote_inclusao_w
	and	nr_seq_titular_solic = nr_seq_inclusao_benef_p;
BEGIN

select	nm_pessoa_fisica,
	nr_seq_lote_inclusao
into STRICT	nm_pessoa_fisica_w,
	nr_seq_lote_inclusao_w
from 	pls_inclusao_beneficiario
where	nr_sequencia = nr_seq_inclusao_benef_p;

update	pls_inclusao_beneficiario
set	nr_seq_lote_inclusao	 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_inclusao_benef_p;

for c01_w in C01 loop
	begin
	update	pls_inclusao_beneficiario
	set	nr_seq_lote_inclusao	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_seq_titular_solic	= CASE WHEN nr_seq_titular_solic = NULL THEN  nr_seq_inclusao_benef_p  ELSE nr_seq_titular_solic END
	where	nr_sequencia		= c01_w.nr_sequencia;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
