CREATE OR REPLACE PROCEDURE pls_remover_partic_analise ( nr_seq_conta_proc_p bigint, nr_seq_analise_p bigint, nr_seq_grupo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
ds_medico_w			varchar(255);
ds_seq_grau_partic_w		varchar(255);
ds_observacao_w			varchar(255);
cd_medico_w			varchar(25);
nr_seq_conta_w			bigint;
nr_seq_analise_conta_item_w	bigint;			
				 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		coalesce(nr_seq_prestador, cd_medico), 
		coalesce(pls_obter_dados_prestador(nr_seq_prestador,'N'), obter_nome_medico(cd_medico,'N')), 
		pls_obter_grau_participacao(nr_seq_grau_partic) 
	from	pls_proc_participante 
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p 
	and	coalesce(ie_atualizado,'N')	= 'N';

C02 CURSOR FOR 
	SELECT 	nr_sequencia 
	from  pls_analise_conta_item 
	where	nr_seq_proc_partic = nr_sequencia_w 
	and	nr_Seq_analise = nr_seq_analise_p;

BEGIN
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	cd_medico_w, 
	ds_medico_w, 
	ds_seq_grau_partic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ds_observacao_w := 'O participante '||cd_medico_w||' - '||ds_medico_w||', que desempenhava a função '||ds_seq_grau_partic_w||', foi excluido do procedimento.';
	 
	select	max(nr_seq_conta) 
	into STRICT	nr_seq_conta_w 
	from	pls_conta_proc 
	where	nr_sequencia = nr_seq_conta_proc_p;
	 
	CALL pls_inserir_hist_analise(nr_seq_conta_w, nr_seq_analise_p, 21, 
			 nr_seq_conta_proc_p, 'P', null, 
			 null, ds_observacao_w, nr_seq_grupo_p,	 
			 nm_usuario_p, cd_estabelecimento_p);
	/* 
	select	max(nr_sequencia) 
	into	nr_seq_analise_conta_item_w 
	from 	pls_analise_conta_item 
	where	nr_seq_proc_partic = nr_Sequencia_w; 
	*/
 
	 
	open C02;
	loop 
	fetch C02 into 
		nr_seq_analise_conta_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		delete 	FROM pls_analise_parecer_item 
		where	nr_seq_item = nr_seq_analise_conta_item_w;	
		end;
	end loop;
	close C02;
	 
	delete 	FROM pls_analise_conta_item 
	where	nr_seq_proc_partic = nr_sequencia_w 
	and	nr_Seq_analise = nr_seq_analise_p;
	 
	delete	FROM pls_ocorrencia_benef	 
	where	coalesce(nr_seq_guia_plano::text, '') = '' 
	and	coalesce(nr_seq_requisicao::text, '') = '' 
	and	coalesce(nr_seq_conta_pos_estab::text, '') = '' 
	and	nr_seq_proc_partic	= nr_sequencia_w;	
	 
	delete	FROM pls_conta_glosa 
	where	nr_seq_proc_partic	= nr_sequencia_w;	
	 
	delete	FROM pls_proc_participante 
	where	nr_sequencia		= nr_sequencia_w;	
	 
	delete	FROM w_pls_resumo_conta 
	where	ie_tipo_item		= 'R' 
	and	nr_seq_partic_proc	= nr_sequencia_w	 
	and	nr_Seq_analise = nr_seq_analise_p;
	 
	end;
end loop;
close C01;
 
update	pls_proc_participante 
set	ie_atualizado 		= 'N' 
where	nr_seq_conta_proc	= nr_seq_conta_proc_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
