CREATE OR REPLACE PROCEDURE pls_remover_proc_partic_ptu ( nr_seq_conta_proc_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_item_proc_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	coalesce(nr_seq_conta,0) 	> 0;


BEGIN

delete	FROM pls_conta_glosa
where	nr_seq_conta_proc  	= nr_seq_conta_proc_p;

delete	FROM pls_ocorrencia_benef
where	nr_seq_proc  		= nr_seq_conta_proc_p
and	coalesce(nr_seq_conta,0) 	> 0;

open C01;
loop
fetch C01 into
	nr_seq_item_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM pls_analise_parecer_item
	where	nr_seq_item	= nr_seq_item_proc_w;

	delete	FROM pls_analise_conta_item
	where	nr_sequencia	= nr_seq_item_proc_w;

	end;
end loop;
close C01;

delete	FROM pls_conta_proc
where	nr_sequencia = nr_seq_conta_proc_p;

delete	FROM w_pls_resumo_conta
where	nr_seq_item 	= nr_seq_conta_proc_p
and	ie_tipo_item	= 'R';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
