SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_reprovar_inc_benef_lote ( nr_seq_lote_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_inco_w			integer;
nr_seq_solicitacao_w		pls_inclusao_beneficiario.nr_sequencia%type;
nr_seq_intercambio_w		pls_lote_inclusao_benef.nr_seq_intercambio%type;

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	pls_inclusao_beneficiario a
	where	nr_seq_lote_inclusao	= nr_seq_lote_p
	and	ie_status in ('I', 'P')
	order by nr_sequencia;

BEGIN

select	max(nr_seq_intercambio)
into STRICT	nr_seq_intercambio_w
from	pls_lote_inclusao_benef
where	nr_sequencia	= nr_seq_lote_p;

select 	count(1)
into STRICT	qt_inco_w
from 	pls_inclusao_beneficiario a
where 	a.nr_seq_lote_inclusao 	= nr_seq_lote_p
and 	ie_status not in ('RO', 'I', 'P');

if (qt_inco_w = 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_solicitacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	pls_inclusao_beneficiario
		set	ie_status		= CASE WHEN nr_seq_intercambio_w = NULL THEN 'RO'  ELSE ie_status END ,
			ie_status_intercambio	= CASE WHEN nr_seq_intercambio_w = NULL THEN ie_status_intercambio  ELSE 'RO' END ,
			ie_status_mov		= '3',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			nr_seq_motivo_rejeicao	= nr_seq_motivo_p,
			dt_rejeicao		= clock_timestamp(),
			ds_observacao		= ds_observacao_p
		where	nr_sequencia		= nr_seq_solicitacao_w
		and	ie_status		<> 'RO';

		end;
	end loop;
	close C01;
else
	wheb_mensagem_pck.exibir_mensagem_abort(391810);
end if;

update	pls_lote_inclusao_benef
set	ie_situacao		= 'RO',
	nr_seq_motivo_rejeicao	= nr_seq_motivo_p
where	nr_sequencia		= nr_seq_lote_p;

CALL pls_gerar_comunic_externo_web(nr_seq_lote_p, '10', nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
