CREATE OR REPLACE PROCEDURE pls_reprovar_proposta ( nr_seq_proposta_p bigint, cd_motivo_reprovacao_p bigint, nr_seq_motivo_rej_incl_p bigint default null, ds_observacao_p text default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE

 
nr_seq_beneficiario_w		pls_proposta_beneficiario.nr_sequencia%type;
nr_seq_inclusao_benef_w		pls_inclusao_beneficiario.nr_sequencia%type;
ie_rejeitar_solic_incl_w	varchar(1);
				
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_inclusao_benef 
	from	pls_proposta_beneficiario 
	where	nr_seq_proposta	= nr_seq_proposta_p;


BEGIN 
 
ie_rejeitar_solic_incl_w := obter_valor_param_usuario(1232, 116, Obter_Perfil_Ativo, nm_usuario_p, obter_estabelecimento_ativo);
 
update	pls_proposta_adesao 
set	ie_status		= 'R', 
	cd_motivo_reprovacao	= cd_motivo_reprovacao_p, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp(), 
	dt_reprovacao		= clock_timestamp(), 
	ds_observacao		= ds_observacao_p 
where	nr_sequencia		= nr_seq_proposta_p;
 
/*aaschlote 02/05/2011 OS - 314422 - Gerar historico para o beneficiário*/
 
open C01;
loop 
fetch C01 into 
	nr_seq_beneficiario_w, 
	nr_seq_inclusao_benef_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL pls_gravar_histor_prop_benef(nr_seq_beneficiario_w,clock_timestamp(),'6','Reprovação da proposta de adesão',nm_usuario_p);
		 
	if (nr_seq_inclusao_benef_w IS NOT NULL AND nr_seq_inclusao_benef_w::text <> '') then 
		if (coalesce(ie_rejeitar_solic_incl_w,'N') = 'S') then 
			CALL pls_reprovar_inclusao_benef(nr_seq_inclusao_benef_w, nr_seq_motivo_rej_incl_p, ds_observacao_p, 'T', null, nm_usuario_p);
			CALL pls_gerar_comunic_externo_web(nr_seq_inclusao_benef_w, '1', nm_usuario_p);
		end if;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
