CREATE OR REPLACE PROCEDURE pls_req_final_proces_web_manut (nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE




ie_origem_solic_w               pls_requisicao.ie_origem_solic%Type;
ie_req_web_finalizada_w         pls_requisicao.ie_req_web_finalizada%type;
ds_historico_w                  varchar(255);


BEGIN
        begin
        select	ie_req_web_finalizada,
                ie_origem_solic
        into STRICT	ie_req_web_finalizada_w,
                ie_origem_solic_w
        from	pls_requisicao
        where	nr_sequencia = nr_seq_requisicao_p;
        exception
        when others then
                ie_req_web_finalizada_w	:= null;
                ie_origem_solic_w	:= null;
        end;

if	(( coalesce(ie_origem_solic_w,'M') = 'P') and (coalesce(ie_req_web_finalizada_w,'N') = 'N')) then

        update  pls_requisicao
        set     ie_req_web_finalizada = 'S' ,
                dt_atualizacao = clock_timestamp(),
                nm_usuario = nm_usuario_p
        where   nr_sequencia = nr_seq_requisicao_p;

        ds_historico_w := 'Requisição não finalizada no portal Web, processo de finalização realizado na função OPS - Manutenção de Processos Operacionais do Autorizador';
        CALL pls_requisicao_gravar_hist(nr_seq_requisicao_p, 'L', ds_historico_w, null, nm_usuario_p);

        commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
