CREATE OR REPLACE PROCEDURE pls_rescindir_contr_benef_mig ( ie_rescindir_contrato_mig_p text, nr_seq_contrato_p bigint, dt_migracao_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

if (ie_rescindir_contrato_mig_p = 'S') then
	update	pls_contrato
	set	dt_rescisao_contrato	= CASE WHEN dt_rescisao_contrato = NULL THEN dt_migracao_p  ELSE dt_rescisao_contrato END ,
		ie_situacao		= '3',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_contrato_p;

	CALL pls_preco_beneficiario_pck.atualizar_desconto_benef(nr_seq_contrato_p, clock_timestamp(), null, 'N', nm_usuario_p, cd_estabelecimento_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
