CREATE OR REPLACE PROCEDURE pls_rescindir_sca_benef ( nr_seq_sca_vinculo_p bigint, dt_rescisao_sca_p timestamp, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


dt_fim_vigencia_w	timestamp;
nr_seq_plano_w		pls_sca_vinculo.nr_seq_plano%type;
nr_seq_segurado_w	pls_sca_vinculo.nr_seq_segurado%type;
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
qt_segurados_w		integer;


BEGIN

select	max(dt_fim_vigencia),
	max(nr_seq_plano),
	max(nr_seq_segurado)
into STRICT	dt_fim_vigencia_w,
	nr_seq_plano_w,
	nr_seq_segurado_w
from	pls_sca_vinculo
where	nr_sequencia	= nr_seq_sca_vinculo_p;

select	count(1)
into STRICT	qt_segurados_w
from	pls_segurado a,
	pls_sca_regra_contrato b
where	a.nr_sequencia 	   = nr_seq_segurado_w
and	a.nr_seq_contrato  = b.nr_seq_contrato
and	b.nr_seq_plano     = nr_seq_plano_w
and	coalesce(a.dt_rescisao::text, '') = ''
and	coalesce(b.ie_permite_rescindir,'S') = 'N';

if (qt_segurados_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 316654, null );
end if;

if	((coalesce(dt_fim_vigencia_w::text, '') = '') or (dt_fim_vigencia_w > dt_rescisao_sca_p)) then
	update	pls_sca_vinculo
	set	dt_fim_vigencia	= dt_rescisao_sca_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_sca_vinculo_p;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
